import React from "react";
import axios from 'axios';

const Weather = () => {
    
    const [latitude, setLatitude] = React.useState('');
    const [longitude, setLongitude] = React.useState('');
    const [city, setCity] = React.useState('');

    const getWeatherDesc = () => {
        axios.get(`http://localhost:3001/coordinates/${latitude}/${longitude}`)
        .then((res) => {
            console.log(res.data);
        })
        .catch(() => {
            console.error('error');
        });
    }
    
    const getLongAndLat = () => {
        axios.get(`http://localhost:3001/location/${city}`)
        .then((res) => {
            console.log(res.data);
            setLatitude(res.data.latitude);
            setLongitude(res.data.longitude);
            getWeatherDesc();
        })
        .catch(() => {
            console.error('error');
        });
    }

    const handleSubmit = (event) => {
        console.log(event.target.elements.city.value);
    }

    return (
        <div>
            <form>
                <input type="text" placeholder="enter your city" name="city"
                    on
                />
                <button onSubmit={handleSubmit}>Submit</button>
            </form>
            <span>Your longitude and latitude are {longitude}, {latitude}</span>
        </div>    
    )
}
export default Weather;
