import React, { useRef } from 'react';

const ChatBox = () => {

    const chat = ['hello', "how are you"]
    const chatDisplay = chat.map(msgs => React.createElement('p', null, msgs))
    const textRef = useRef();

    const sendMsg = () => {
        chat.push(textRef.current.value)
    }

    return(
        <div className="chat-box">
            <div className="msgs">
            {chatDisplay}
            </div>
            <form className="msg-form">
                <input id="text-field" ref={textRef} type='text' name="msgField" placeholder='send message'/>
                <button id="send-btn" type='button' onClick>send</button>
            </form>
        </div>
    )
}
export default ChatBox;
