import React from 'react';
import axios from 'axios';

const Map = () => {

    const [city, setCity] = React.useState('');
    const [state, setState] = React.useState('');
    const [map, setMap] = React.useState('');

    const getMap= async () => {
        try {
            const getData = await axios.get(`https://www.google.com/maps/embed/v1/place?key=AIzaSyAxrGSdDukOL15sBwLLANcanGnQD8KJzaw&q=${city}, ${state}`);
            setCity(prevCity => prevCity = getData.data.city.name);
        } catch (err) {
            console.log(err);
        }
    }

    React.useEffect(() => {
        const getCity = async () => {
            try {
                const getData = await axios.get(`http://192.168.1.79:3001/location/`);
                setCity(prevCity => prevCity = getData.data.city.name);
            } catch (err) {
                console.log(err);
            }
        }
        getCity();
        const getState = async () => {
            try {
                const getData = await axios.get(`http://192.168.1.79:3001/location/`);
                setState(prevState => prevState = getData.data.area.name);
            } catch (err) {
                console.log(err);
            }
        }
        getState();
    }, [city, state])

    console.log('the city is ' + city);
    console.log('the state is ' + state);

    return(
        <div className="map-screen">
            <iframe
                id="map-view"
                className="map-view"
                width="850px"
                height="600px"
                src={`https://www.google.com/maps/embed/v1/place?key=AIzaSyAxrGSdDukOL15sBwLLANcanGnQD8KJzaw
                    &q=${city}, ${state}`}>
            </iframe>
        </div>
    )
}
export default Map;
