import axios from 'axios';

const Weather = () => {
    
    const [latitude, setLatitude] = React.useState('');
    const [longitude, setLongitude] = React.useState('');
    const [description , setDescription] = React.useState('');

    /*const getLocation = async () => {
        await axios.get(`http://localhost:3001/coordinates/${latitude}/add/${longitude}`)
        .then((res) => {
            console.log(res.data);
            setDescription(prevDescription => prevDescription = res.data);
        })
        .catch((error) => {
            console.log('error');
        });
    }
    
    const getLongAndLat = async () => {
        await axios.get(`http://localhost:3001/location/`)
        .then((res) => {
            setLongitude(prevLongitude => prevLongitude = res.data.longitude);
            setLatitude(prevLatitude => prevLatitude = res.data.latitude);
            console.log(latitude);
            console.log(longitude);
            getLocation();
        })
        .catch((error) => {
            console.log('error');
        });
    }*/

    const getLocation = async () => {
        const getData = await axios.get(`http://localhost:3001/coordinates/${latitude}/add/${longitude}`)
        console.log(getData);
        setDescription(prevDescription => prevDescription = getData.data);
    }

    

    React.useEffect(() => {
        getLongAndLat();
    }, [])


    return (
        <div className="weather">
            <span>Your longitude and latitude coordinates are :</span>
            <span id="coords">{latitude}, {longitude}</span>
            <span>{description}</span>
        </div>    
    )
}
export default Weather;
