import React, { useRef } from 'react';
import io from 'socket.io-client';

const ChatBox = () => {

    const [msg, setMsg] = React.useState('')
    const [chat, setChat] = React.useState([])
    const chatDisplay = chat.map(msgs => <p>{msgs}</p>)

    const textRef = useRef();

    //create socket
    const socket = io.connect("http://localhost:3001");

    const sendMsg = () => {
        socket.emit('send_message', { message: textRef.current.value });
    }

    React.useState(() => {
        socket.on("recieve_message", (data) => {
            setMsg
            setChat(prevChat => [...prevChat, data]);
        })
    }, [socket, chat])

    return(
        <div className="chat-box">
            <div className="msgs">
            {chatDisplay}
            </div>
            <form className="msg-form">
                <input id="text-field" ref={textRef} type='text' placeholder='send message'/>
                <button id="send-btn" type='button' onClick={sendMsg}>send</button>
            </form>
        </div>
    )
}
export default ChatBox;
